// Importer la fonction Router pour la création des routes
import { Router } from 'express';

// Importer les controllers de Category
import { 
    getAllCategories, 

    createCategory, 
    updateCategory, 
    deleteCategory 
} from "../controllers/Category1Controller.js";

import {
    validateCreateCategory,
    validateUpdateCategory,
    validateDeleteCategory,
    
  } from '../validations/CategoryValidation.js';
  

// Création d'une instance de Router
const Categoryroute = Router();

Categoryroute.get('/', getAllCategories)
    
    .post('/', validateCreateCategory, createCategory)
    .put('/:id', validateUpdateCategory ,updateCategory)
    .delete('/:id', validateDeleteCategory ,deleteCategory);

export default Categoryroute;
