// Importer la fonction Router pour la création des routes
import { Router } from "express";

// Importer les controllers de Client
import { 
    getAllClients, 
   
    createClient, 
    updateClient, 
    deleteClient 
} from "../controllers/ClientController.js";

import {
    validateCreateClient,
    validateUpdateClient,
    validateDeleteClient,
   
  } from '../validations/ClientValidation.js'; 
// Création d'une instance de Router
const Clientroute = Router();

Clientroute.get('/', getAllClients)
    
    .post('/',validateCreateClient,  createClient)
    .put('/:id',validateUpdateClient,  updateClient)
    .delete('/:id',validateDeleteClient, deleteClient);

export default Clientroute;
