// Importer la fonction Router pour la création des routes
import { Router } from "express";

// Importer les controllers de PaymentMethod
import { 
    getAllPaymentMethods, 
    getPaymentMethodById, 
    createPaymentMethod, 
    updatePaymentMethod, 
    deletePaymentMethod 
} from "../controllers/methodepaimentController.js";

import {
    validateCreatePaymentMethod,
    validateUpdatePaymentMethod,
    validatePaymentMethodId,
  } from '../validations/MethodedepaimentValidation.js';

// Création d'une instance de Router
const methoderoute = Router();

methoderoute.get('/', getAllPaymentMethods)
    .get('/:id',validatePaymentMethodId,  getPaymentMethodById)
    .post('/',  validateCreatePaymentMethod, createPaymentMethod)
    .put('/:id',validatePaymentMethodId, validateUpdatePaymentMethod, updatePaymentMethod)
    .delete('/:id',validatePaymentMethodId, deletePaymentMethod);

export default methoderoute;
