// Importer la fonction Router pour la création des routes
import { Router } from "express";

// Importer les controllers de Product
import { 
    getAllProducts, 
    getProductById, 
    createProduct, 
    updateProduct, 
    deleteProduct 
} from "../controllers/productController.js";
import {
    validateCreateProduct,
    validateUpdateProduct,
    validateProductId,
  } from '../validations/ProductValidation.js';
// Création d'une instance de Router
const productroute = Router();

productroute.get('/', getAllProducts)       // Obtenir tous les produits
    .get('/:id', validateProductId, getProductById)      // Obtenir un produit par son ID
    .post('/',validateCreateProduct, createProduct)         // Créer un nouveau produit
    .put('/:id',  validateProductId, validateUpdateProduct ,updateProduct)       // Mettre à jour un produit par son ID
    .delete('/:id',  validateProductId,deleteProduct);   // Supprimer un produit par son ID

export default productroute;
