//// Import the Router function for creating routes
import { Router } from "express";

// Import order controllers
import { 
    createOrder, 
    getAllOrders, 
    getOrderById, 
    updateOrder, 
    deleteOrder 
} from "../controllers/commandeController.js";



import {
    validateCreateCommande,
    validateUpdateCommande,
    validateCommandeId,
    
  } from '../validations/CommandeValidation.js'; 
// Create an instance of Router
const commendroute = Router();

// Define order routes
commendroute.post('/',validateCreateCommande,  createOrder)              // Create a new order
     .get('/', getAllOrders)              // Get all orders with pagination
     .get('/:id', validateCommandeId, getOrderById)           // Get a specific order by ID
     .put('/:id', validateCommandeId, validateUpdateCommande, updateOrder)            // Update an order by ID
     .delete('/:id', validateCommandeId,deleteOrder);        // Delete an order by ID

export default commendroute;