// Import the Router function for creating routes
import { Router } from "express";

// Import employee controllers
import { 
  getAllEmployees, 
  getEmployeeById, 
  createEmployee, 
  updateEmployee, 
  deleteEmployee 
} from "../controllers/employeController.js";
import {
   validateCreateEmployee,
   validateUpdateEmployee,
   validateEmployeeId 
  } from '../validations/EmployerValidation.js';
// Create an instance of Router
const emoloyeroute = Router();

// Define employee routes
emoloyeroute.get('/', getAllEmployees)             // Get all employees with pagination
     .get('/:id', validateEmployeeId ,getEmployeeById)          // Get a specific employee by ID
     .post('/', validateCreateEmployee,  createEmployee)             // Create a new employee
     .put('/:id',validateEmployeeId, validateUpdateEmployee, updateEmployee)           // Update an employee by ID
     .delete('/:id', validateEmployeeId, deleteEmployee);       // Delete an employee by ID

export default emoloyeroute;