// Importer la fonction Router pour la création des routes
import { Router } from "express";

// Importer les controllers de PaymentTransaction
import { 
    getAllTransactions, 
    getTransactionById, 
    createTransaction, 
    updateTransaction, 
    deleteTransaction 
} from "../controllers/transactionController.js";

import {
    validateCreateTransaction,
    validateUpdateTransaction,
    validateTransactionId,
  } from '../validations/TransactionValidation.js';

// Création d'une instance de Router
const transactionroute = Router();

transactionroute.get('/', getAllTransactions)
    .get('/:id', validateTransactionId, getTransactionById)
    .post('/', validateCreateTransaction, createTransaction)
    .put('/:id', validateTransactionId, validateUpdateTransaction, updateTransaction)
    .delete('/:id', validateTransactionId, deleteTransaction);

export default transactionroute;
